# -*- coding: utf-8 -*-
#！/bin/env python3
import os
import coin.utils
import coin.config

def run(name):
    config = coin.config.Configration()
    config.load()
    if len(name) > 0:
        n = name[0]
        pkg_info = config.read_package(n)
        if not pkg_info:
            coin.utils.error(f"package {n} not found")
            return

        run_info = pkg_info['run']
        
        if 'cmd' in run_info:
            cmd = run_info['cmd']
            os.system(cmd)
