#ï¼/bin/env python3
import coin.utils as cu
import coin.config
import os
import sys
import importlib

def solution(name):
    cu.info(f"Solution {name}")
    config = coin.config.Configration()
    config.load()
    if len(name) > 0:
        n = name[0]
        if os.path.exists(cu.solution_path(n)) == False:
            cu.error(f"Solution {n} not found")
            sys.exit(1)
        sys.path.append(os.path.join(cu.solution_path(n), 'lib/python/site-packages'))
        solution = importlib.import_module(n)
        solution.execute()
